Censored MLE function

 cMLE &lt;- function(params, y.obs, y.cens = NULL){

   mu = params[1]
   sigma = params[2]


    obs_ll &lt;- sum(dnorm(y.obs, mean = mu, sd = sigma, log = TRUE))
    cens_ll &lt;- sum(pnorm(y.cens, mean = mu, sd = sigma, log.p = TRUE, lower.tail = TRUE))
    nll &lt;- -1 * (obs_ll + cens_ll)

    return(nll)

 }
